import {
  useState,
  useEffect,
  useContext,
  createContext,
  useReducer,
} from "react"
import { favoriteReducer } from "../../reducer/favoriteReducer"
import { themeReducer } from "../../reducer/themeReducer"

export const initialState = { theme: "light", data: [] }

export const ContextGlobal = createContext()

export const ContextProvider = ({ children }) => {
  const [dentists, setDentists] = useState([])
  const [state, dispatch] = useReducer(themeReducer, initialState)
  const [favoriteDentists, dispatchFavorites] = useReducer(
    favoriteReducer,
    [],
    initFav
  )

  function initFav(initialFavState) {
    return localStorage.getItem("favs")
      ? JSON.parse(localStorage.getItem("favs"))
      : initialFavState
  }

  // Obtener los dentistas desde la API y almacenarlos en el estado global
  useEffect(() => {
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        dispatch({ type: 'SET_DENTISTS', payload: data });
      } catch (error) {
        console.log('Error al obtener los dentistas:', error);
      }
    };

    fetchDentists();
  }, []);

  return (
    <AppContext.Provider value={{ state, dispatch }}>
      {children}
    </AppContext.Provider>
  );
};
