import React, { createContext, useEffect, useReducer } from 'react';


// Definir el estado inicial
const initialState = {
  theme: 'light',
  dentists: [],
  favorites: [],
};

// Reducer para manejar los cambios de estado
const reducer = (state, action) => {
  switch (action.type) {
    case 'SET_THEME':
      return { ...state, theme: action.payload };
    case 'SET_DENTISTS':
      return { ...state, dentists: action.payload };
    case 'ADD_TO_FAVORITES': // Agrega este caso
      return { ...state, favorites: [...state.favorites, action.payload] };
    default:
      return state;
  }
};

// Crear el contexto global
export const AppContext = createContext();

// Componente proveedor del contexto global
export const AppProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  // Obtener los dentistas desde la API y almacenarlos en el estado global
  useEffect(() => {
    const fetchDentists = async () => {
      try {
        const response = await fetch('https://jsonplaceholder.typicode.com/users');
        const data = await response.json();
        dispatch({ type: 'SET_DENTISTS', payload: data });
      } catch (error) {
        console.log('Error al obtener los dentistas:', error);
      }
    };

    fetchDentists();
  }, []);

  return (
    <AppContext.Provider value={{ state, dispatch }}>
      {children}
    </AppContext.Provider>
  );
};
