import React, { useState } from "react";
import '../index.css'; 

const Form = () => {
  const [fullName, setFullName] = useState('');
  const [email, setEmail] = useState('');
  const [error, setError] = useState('');
  const [successMessage, setSuccessMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones
    if (fullName.length < 5 || !emailIsValid(email)) {
      setError('Por favor verifique su información nuevamente');
      setSuccessMessage('');
    } else {
      setError('');
      setSuccessMessage(`Gracias ${fullName}, te contactaremos pronto vía email`);
      console.log('Datos enviados:', { fullName, email });
    }
  };

  const emailIsValid = (email) => {
    return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email);
  };

  return (
    <div>
       <form onSubmit={handleSubmit} className="form__contact">
        <input placeholder="Your name..." type="text" ref={setFullName} />
        <input placeholder="Your email..." type="email" ref={setEmail} />
        <button className="form-button">Enviar</button>
      </form>
      {error && <p className="error">{error}</p>}
      {successMessage && <p className="success">{successMessage}</p>}
    </div>
  );
};

export default Form;
